document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("menu-toggle"),t=document.getElementById("menu-close"),s=document.getElementById("mobile-menu"),n=document.getElementById("menu-backdrop"),c=()=>{s.classList.remove("hidden"),n.classList.remove("hidden"),e.setAttribute("aria-expanded","true")},d=()=>{s.classList.add("hidden"),n.classList.add("hidden"),e.setAttribute("aria-expanded","false")};e.addEventListener("click",c),t==null||t.addEventListener("click",d),n.addEventListener("click",d),document.addEventListener("keydown",o=>{o.key==="Escape"&&d()})});
